  static ImportRobot(robotStr) {
    var decoder = new Base64Decoder();
    decoder.decode(robotStr);
    var b = decoder.toByteArray();
    b.uncompress();
    b.readUTF();
    b.readUTF();
    b.readInt();
    b.readInt();
    ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotPublic = true;
    ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotEditable = true;
    b.readInt();
    return Database.ExtractRobotFromByteArray(b);
  }

  static ImportReplay(replayStr) {
    var decoder = new Base64Decoder();
    decoder.decode(replayStr);
    var b = decoder.toByteArray();
    b.uncompress();
    var replayLength = b.readInt();
    var robotLength = b.readInt();
    var replayData = new ByteArray();
    var robotData = new ByteArray();

    while (b.position < replayLength + 8) {
      replayData.writeByte(b.readByte());
    }

    replayData.uncompress();

    while (b.position < replayLength + robotLength + 8) {
      robotData.writeByte(b.readByte());
    }

    robotData.uncompress();
    var replay = Database.ExtractReplayFromByteArray(replayData);
    var robot = Database.ExtractRobotFromByteArray(robotData);
    b.readUTF();
    b.readUTF();
    b.readUTF();
    b.readInt();
    b.readUTF();
    ControllerGameGlobals_1.ControllerGameGlobals.potentialReplayPublic = true;
    return [replay, robot];
  }

