  static FinishLoadingRobot(e) {
    Database.StopTimer();
    var responseData = Database.StripNewlinesAndExtractByte(e.target.data);

    if (responseData.length == 6 || responseData.length == 7) {
      var errorStr = responseData.readUTFBytes(5);

      if (errorStr == "ERROR") {
        Database.ErrorHandler(new Event(""), Database.ERROR_MESSAGES[parseInt(responseData.readUTFBytes(responseData.bytesAvailable))]);
        return null;
      } else {
        responseData.position = 0;
      }
    } else if (responseData.length == 0) {
      Database.ErrorHandler(new Event(""), Database.NO_DATA_MESSAGE);
      return null;
    }

    try {
      var robotLengthStr = "";

      while (responseData.position != responseData.length) {
        var b = responseData.readByte();
        if (b == 10) break;else robotLengthStr = robotLengthStr.concat(String.fromCharCode(b));
      }

      var robotLength = parseInt(robotLengthStr);
      responseData = Database.StripNewlines(responseData);
      var robotData = new ByteArray();

      for (var i = 0; i < robotLength; i++) {
        robotData.writeByte(responseData.readByte());
      }

      robotData.uncompress();
      robotData.position = 0;
      var robot = Database.ExtractRobotFromByteArray(robotData);
      var challengeData = new ByteArray();

      while (responseData.position != responseData.length) {
        challengeData.writeByte(responseData.readByte());
      }

      if (challengeData.length > 0) {
        challengeData.uncompress();
        challengeData.position = 0;
        robot.challenge = Database.ExtractChallengeFromByteArray(challengeData);
      }

      return robot;
    } catch (e) {
      Database.ErrorHandler(new Event(""), Database.EXCEPTION_MESSAGE);
    }

    return null;
  }

