  ShowObjectPanel(shape) {
    this.visible = true;
    this.m_colourWindow.visible = false;
    this.m_fixedJointPanel.visible = false;
    this.m_jointEditPanel.visible = false;
    this.m_objectEditPanel.visible = true;
    this.m_textEditPanel.visible = false;
    this.m_thrustersEditPanel.visible = false;
    this.m_multiEditPanel.visible = false;
    this.m_buildBoxPanel.visible = false;
    this.m_cannonPanel.visible = false;
    this.m_densitySlider.value = shape.density;
    this.m_densitySlider.minValue = ControllerGameGlobals_1.ControllerGameGlobals.minDensity;
    this.m_densitySlider.maxValue = ControllerGameGlobals_1.ControllerGameGlobals.maxDensity;
    this.m_densityArea.text = shape.density.toString();
    this.m_collisionBox.selected = shape.collide;
    this.m_collisionBox.visible = this.cont.controllerType === 'sandbox' || this.cont.controllerType === 'challenge' && (ControllerChallenge.challenge.nonCollidingAllowed || !ControllerChallenge.playChallengeMode);
    this.m_fixateBox.selected = shape.isStatic;
    this.m_fixateBox.visible = this.cont.conditionsButton === 'sandbox' || this.cont.controllerType === 'challenge' && (ControllerChallenge.challenge.fixateAllowed || !ControllerChallenge.playChallengeMode);
    this.m_cameraBox.selected = shape.isCameraFocus;
    this.m_outlineBox.selected = shape.outline;
    this.m_terrainBox.selected = shape.terrain;
    this.m_undragableBox.selected = shape.undragable;
    if (shape instanceof Circle_1.Circle) this.m_shapeHeader.text = "Circle";
    if (shape instanceof Rectangle_1.Rectangle) this.m_shapeHeader.text = "Rectangle";
    if (shape instanceof Triangle_1.Triangle) this.m_shapeHeader.text = "Triangle";
    var format = new pixi_js_1.TextStyle();
    format.fontFamily = Main_1.Main.GLOBAL_FONT;
    format.fontSize = 14;
    format.align = 'center';
    this.m_shapeHeader.style = format;
  }

