      ControllerGameGlobals_1.ControllerGameGlobals.minDensity = this.challenge.minDensity == -Number.MAX_VALUE ? 1 : this.challenge.minDensity;
      ControllerGameGlobals_1.ControllerGameGlobals.maxDensity = this.challenge.maxDensity == Number.MAX_VALUE ? 30 : this.challenge.maxDensity;
      ControllerGameGlobals_1.ControllerGameGlobals.maxRJStrength = this.challenge.maxRJStrength == Number.MAX_VALUE ? 30 : this.challenge.maxRJStrength;
