  CheckIfPartsFit() {
    var partsToCheck = this.allParts.filter(this.PartIsEditable);
    var numAreas = this.NumBuildingAreas();
    this.partsFit = true;
    if (this.controllerType === 'sandbox') return;
    if (this.controllerType === 'challenge' && !ControllerChallenge.playChallengeMode) return;
    if (numAreas == 0) return; // Make sure the parts fit in the allowed building area

    var minX;
    var maxX;
    var minY;
    var maxY;

    for (var i = 0; i < partsToCheck.length; i++) {
      var partFits = false;

      for (var j = 0; j < numAreas; j++) {
        var buildingArea = this.GetBuildingAreaNumber(j);

        if (partsToCheck[i] instanceof Circle_1.Circle) {
          minX = partsToCheck[i].centerX - partsToCheck[i].radius;
          maxX = partsToCheck[i].centerX + partsToCheck[i].radius;
          minY = partsToCheck[i].centerY - partsToCheck[i].radius;
          maxY = partsToCheck[i].centerY + partsToCheck[i].radius;
        } else if (partsToCheck[i] instanceof Rectangle_1.Rectangle || partsToCheck[i] instanceof Triangle_1.Triangle || partsToCheck[i] instanceof Cannon_1.Cannon) {
          var verts = partsToCheck[i].GetVertices();
          minX = Number.MAX_VALUE;
          minY = Number.MAX_VALUE;
          maxX = -Number.MAX_VALUE;
          maxY = -Number.MAX_VALUE;

          for (var k = 0; k < verts.length; k++) {
            minX = Math.min(minX, verts[k].x);
            maxX = Math.max(maxX, verts[k].x);
            minY = Math.min(minY, verts[k].y);
            maxY = Math.max(maxY, verts[k].y);
          }
        } else {
          minX = Number.MAX_VALUE;
          minY = Number.MAX_VALUE;
          maxX = -Number.MAX_VALUE;
          maxY = -Number.MAX_VALUE;
        }

        if (minX >= buildingArea.lowerBound.x && minY >= buildingArea.lowerBound.y && maxX < buildingArea.upperBound.x && maxY < buildingArea.upperBound.y) {
          partFits = true;
          break;
        }
      }

      if (!partFits) {
        this.partsFit = false;
        break;
      }
    }
  }

