  processLoadedRobot(robot) {
    if (robot) {
      var newParts = robot.allParts;

      if (ControllerGameGlobals_1.ControllerGameGlobals.loadAndInsert) {
        ControllerGameGlobals_1.ControllerGameGlobals.loadAndInsert = false;
        var hasCircles = false;
        var hasRects = false;
        var hasTriangles = false;
        var hasFJs = false;
        var hasRJs = false;
        var hasSJs = false;
        var hasThrusters = false;
        var hasCannons = false;
        var hasStatic = false;

        for (var i = 0; i < newParts.length; i++) {
          if (newParts[i].isStatic) hasStatic = true;
          if (newParts[i] instanceof Circle_1.Circle) hasCircles = true;
          if (newParts[i] instanceof Rectangle_1.Rectangle) hasRects = true;
          if (newParts[i] instanceof Triangle_1.Triangle) hasTriangles = true;
          if (newParts[i] instanceof FixedJoint_1.FixedJoint) hasFJs = true;
          if (newParts[i] instanceof RevoluteJoint_1.RevoluteJoint) hasRJs = true;
          if (newParts[i] instanceof PrismaticJoint_1.PrismaticJoint) hasSJs = true;
          if (newParts[i] instanceof Thrusters_1.Thrusters) hasThrusters = true;
          if (newParts[i] instanceof Cannon_1.Cannon) hasCannons = true;
        }

        if (this.controllerType === 'challenge' && (hasStatic && !ControllerChallenge.challenge.fixateAllowed || hasCircles && !ControllerChallenge.challenge.circlesAllowed || hasRects && !ControllerChallenge.challenge.rectanglesAllowed || hasTriangles && !ControllerChallenge.challenge.trianglesAllowed || hasFJs && !ControllerChallenge.challenge.fixedJointsAllowed || hasRJs && !ControllerChallenge.challenge.rotatingJointsAllowed || hasSJs && !ControllerChallenge.challenge.slidingJointsAllowed || hasThrusters && !ControllerChallenge.challenge.thrustersAllowed || hasCannons && !ControllerChallenge.challenge.cannonsAllowed)) {
          this.m_fader.visible = true;
          ControllerGameGlobals_1.ControllerGameGlobals.loadAndInsert = true;
          this.ShowDialog3("Sorry, that robot contains parts that are not allowed in this challenge!");
          this.m_progressDialog.ShowOKButton();
          this.m_progressDialog.StopTimer();
          Main_1.Main.ShowMouse();
          return;
        } else {
          ControllerGameGlobals_1.ControllerGameGlobals.curRobotID = "";
          this.allParts = this.allParts.concat(newParts);
          this.CheckIfPartsFit();
          this.m_progressDialog.SetMessage("Load successful!");
          this.m_progressDialog.HideInXSeconds(1);

          if (this.m_chooserWindow) {
            this.m_chooserWindow.visible = false;
            this.removeChild(this.m_chooserWindow);
            this.m_chooserWindow = null;
          }

          this.m_fader.visible = false;
          ControllerGameGlobals_1.ControllerGameGlobals.curRobotEditable = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotEditable
          /* && ((!hasThrusters && !hasCannons) || Main.premiumMode)*/
          ;
          ControllerGameGlobals_1.ControllerGameGlobals.curRobotPublic = false;
          ControllerGameGlobals_1.ControllerGameGlobals.curRobotFeatured = false;
          this.redrawRobot = true;
          this.CenterOnLoadedRobot();
        }
      } else {
        ControllerGameGlobals_1.ControllerGameGlobals.curRobotID = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotID;
        ControllerGameGlobals_1.ControllerGameGlobals.ratedCurRobot = false;
        ControllerGameGlobals_1.ControllerGameGlobals.curRobotEditable = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotEditable
        /* && ((!hasThrusters && !hasCannons) || Main.premiumMode)*/
        ;
        ControllerGameGlobals_1.ControllerGameGlobals.curRobotPublic = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotPublic;
        ControllerGameGlobals_1.ControllerGameGlobals.curRobotFeatured = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotFeatured;
        ControllerGameGlobals_1.ControllerGameGlobals.curReplayID = "";
        ControllerGameGlobals_1.ControllerGameGlobals.loadedParts = newParts;
        Main_1.Main.changeControllers = true;
        ControllerGameGlobals_1.ControllerGameGlobals.playingReplay = false;
        ControllerGameGlobals_1.ControllerGameGlobals.initX = robot.cameraX;
        ControllerGameGlobals_1.ControllerGameGlobals.initY = robot.cameraY;
        ControllerGameGlobals_1.ControllerGameGlobals.initZoom = robot.zoomLevel;

        if (robot.challenge) {
          Main_1.Main.nextControllerType = 1;
          ControllerChallenge.challenge = robot.challenge;
          ControllerChallenge.playChallengeMode = true;
          ControllerChallenge.playOnlyMode = true;
          ControllerSandbox_1.ControllerSandbox.settings = robot.challenge.settings;
          ControllerGameGlobals_1.ControllerGameGlobals.curChallengeID = ControllerGameGlobals_1.ControllerGameGlobals.potentialChallengeID;
          ControllerGameGlobals_1.ControllerGameGlobals.ratedCurChallenge = false;
          ControllerGameGlobals_1.ControllerGameGlobals.curChallengePublic = false;
          ControllerGameGlobals_1.ControllerGameGlobals.curChallengeFeatured = false;
          ControllerGameGlobals_1.ControllerGameGlobals.justLoadedRobotWithChallenge = true;
        } else {
          Main_1.Main.nextControllerType = 0;
          ControllerSandbox_1.ControllerSandbox.settings = robot.settings;
          ControllerGameGlobals_1.ControllerGameGlobals.curChallengeID = "";
        }
      }
    }
  }

