  processLoadedRobot(robot) {
    var loadedParts = robot.allParts;
    var hasThrusters = false;

    for (var i = 0; i < loadedParts.length; i++) {
      if (loadedParts[i] instanceof Thrusters_1.Thrusters || loadedParts[i] instanceof Cannon_1.Cannon) hasThrusters = true;
    }

    ControllerGameGlobals_1.ControllerGameGlobals.loadedParts = loadedParts;
    ControllerGameGlobals_1.ControllerGameGlobals.curRobotID = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotID;
    ControllerGameGlobals_1.ControllerGameGlobals.ratedCurRobot = false;
    ControllerGameGlobals_1.ControllerGameGlobals.curRobotEditable = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotEditable
    /* && (!hasThrusters || Main.premiumMode)*/
    ;
    ControllerGameGlobals_1.ControllerGameGlobals.curRobotPublic = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotPublic;
    ControllerGameGlobals_1.ControllerGameGlobals.curRobotFeatured = ControllerGameGlobals_1.ControllerGameGlobals.potentialRobotFeatured;
    ControllerGameGlobals_1.ControllerGameGlobals.curReplayID = "";
    ControllerGameGlobals_1.ControllerGameGlobals.initX = robot.cameraX;
    ControllerGameGlobals_1.ControllerGameGlobals.initY = robot.cameraY;
    ControllerGameGlobals_1.ControllerGameGlobals.initZoom = robot.zoomLevel;
    Main_1.Main.changeControllers = true;

    if (robot.challenge) {
      Main_1.Main.nextControllerType = 1;
      ControllerChallenge_1.ControllerChallenge.challenge = robot.challenge;
      ControllerChallenge_1.ControllerChallenge.playChallengeMode = true;
      ControllerChallenge_1.ControllerChallenge.playOnlyMode = true;
      ControllerSandbox_1.ControllerSandbox.settings = robot.challenge.settings;
      ControllerGameGlobals_1.ControllerGameGlobals.curChallengeID = ControllerGameGlobals_1.ControllerGameGlobals.potentialChallengeID;
      ControllerGameGlobals_1.ControllerGameGlobals.ratedCurChallenge = false;
      ControllerGameGlobals_1.ControllerGameGlobals.curChallengePublic = false;
      ControllerGameGlobals_1.ControllerGameGlobals.curChallengeFeatured = false;
      ControllerGameGlobals_1.ControllerGameGlobals.justLoadedRobotWithChallenge = true;
    } else {
      Main_1.Main.nextControllerType = 0;
      ControllerSandbox_1.ControllerSandbox.settings = robot.settings;
      ControllerGameGlobals_1.ControllerGameGlobals.curChallengeID = "";
    }
  }

