  ReloadLoadWindow() {
    this.m_loadWindow.visible = true;

    if (this.m_loadWindow.dataType != SaveLoadWindow.HIGH_SCORE_TYPE && this.m_loadWindow.yourRobotsBox.selected) {
      if (this.m_loadWindow.dataType == SaveLoadWindow.LOAD_ROBOT_TYPE) {
        Database_1.Database.GetRobotData(ControllerGameGlobals_1.ControllerGameGlobals.userName, ControllerGameGlobals_1.ControllerGameGlobals.password, false, Database_1.Database.SORT_BY_EDIT_TIME, Database_1.Database.SORT_PERIOD_ALLTIME, 1, "", this.finishGettingLoadRobotData);
        this.ShowDialog("Getting robots...");
      } else if (this.m_loadWindow.dataType == SaveLoadWindow.LOAD_REPLAY_TYPE) {
        Database_1.Database.GetReplayData(ControllerGameGlobals_1.ControllerGameGlobals.userName, ControllerGameGlobals_1.ControllerGameGlobals.password, false, Database_1.Database.SORT_BY_EDIT_TIME, Database_1.Database.SORT_PERIOD_ALLTIME, 1, "", this.finishGettingLoadReplayData);
        this.ShowDialog("Getting replays...");
      } else {
        Database_1.Database.GetChallengeData(ControllerGameGlobals_1.ControllerGameGlobals.userName, ControllerGameGlobals_1.ControllerGameGlobals.password, false, Database_1.Database.SORT_BY_EDIT_TIME, Database_1.Database.SORT_PERIOD_ALLTIME, 1, "", this.finishGettingLoadChallengeData);
        this.ShowDialog("Getting challenges...");
      }

      this.m_loadWindow.ShowFader();
      Main_1.Main.ShowHourglass();
    } else if (this.m_loadWindow.dataType == SaveLoadWindow.HIGH_SCORE_TYPE) {
      Database_1.Database.GetScoreData(ControllerGameGlobals_1.ControllerGameGlobals.userName, ControllerGameGlobals_1.ControllerGameGlobals.password, Database_1.Database.highScoresChallenge, this.m_loadWindow.m_scoreTypeBox.selectedIndex == 1, this.m_loadWindow.m_scoreTypeBox.selectedIndex == 2, Database_1.Database.SORT_BY_SCORE, 1, "", this.finishGettingScoreData);
      this.ShowDialog("Getting high scores...");
      this.m_loadWindow.ShowFader();
      Main_1.Main.ShowHourglass();
    }
  }

